import React from 'react';
import {View, StyleSheet, Text, Image} from 'react-native';
import {DrawerContentScrollView, DrawerItem} from '@react-navigation/drawer';
import {TouchableOpacity} from 'react-native-gesture-handler';
import {useNavigation} from '@react-navigation/native';
import {faHome} from '@fortawesome/free-solid-svg-icons/faHome';
import {FontAwesomeIcon} from '@fortawesome/react-native-fontawesome';
import {faUserAlt} from '@fortawesome/free-solid-svg-icons/faUserAlt';
import {faRightFromBracket} from '@fortawesome/free-solid-svg-icons/faRightFromBracket';
import {DrawerNavigationProp} from '@react-navigation/drawer';

const DrawerList = [
  {icon: faHome, label: 'Home', navigateTo: 'Home'},
  {icon: faUserAlt, label: 'About', navigateTo: 'About'},
];
const DrawerLayout = ({icon, label, navigateTo}: any) => {
  const navigation = useNavigation<DrawerNavigationProp<any>>();
  return (
    <DrawerItem
      icon={({color, size}) => <FontAwesomeIcon icon={icon} color="#0163d2" />}
      label={label}
      onPress={() => {
        navigation.navigate(navigateTo);
      }}
    />
  );
};

const DrawerItems = (props: any) => {
  return DrawerList.map((el, i) => {
    return (
      <DrawerLayout
        key={i}
        icon={el.icon}
        label={el.label}
        navigateTo={el.navigateTo}
      />
    );
  });
};
const DrawerContent = (props: any) => {
  return (
    <View style={{flex: 1}}>
      <DrawerContentScrollView {...props}>
        <View style={styles.drawerContent}>
          <TouchableOpacity activeOpacity={0.8}>
            <View style={styles.userInfoSection}>
              <View style={{flexDirection: 'row', marginTop: 15}}>
                <Image
                  source={{
                    uri: 'data:image/png;base64,',
                  }}
                />
                <View style={{marginLeft: 10, flexDirection: 'column'}}>
                  <Text style={styles.title}>Sarun AV</Text>
                  <Text style={styles.caption} numberOfLines={1}>
                    sarun@gmail.com
                  </Text>
                </View>
              </View>
            </View>
          </TouchableOpacity>
          <View style={styles.drawerSection}>
            <DrawerItems />
          </View>
        </View>
      </DrawerContentScrollView>
      <View style={styles.bottomDrawerSection}>
        <DrawerItem
          icon={({color, size}) => (
            <FontAwesomeIcon icon={faRightFromBracket} color="#0163d2" />
          )}
          label="Sign Out"
          onPress={() => {}}
        />
      </View>
    </View>
  );
};
export default DrawerContent;

